mapper.select:
 select * from
(select rownum as rnum,a.* from
(select * 
from product 
order by pro_idx desc
) a

) b 
where rnum >=#{start}
and rnum<=#{end}

 select * from
(select rownum as rnum,a.* from
(select * 
from product
where pro_state != 1 
order by pro_idx desc
) a

) b 
where rnum >=#{start}
and rnum<=#{end}

select * from product 
where pro_cate=#{proF}
order by pro_idx desc

mapper.update:
update product
set pro_amount=#{pro_amount}, pro_state=#{pro_state}
where pro_idx=#{pro_idx}

update product
set pro_state=#{pro_state}, pro_amount=#{pro_amount}
where pro_idx=#{pro_idx}

mapper.select:
select pro_amount
from product
where pro_idx=#{pro_idx}

mapper.update:
		update product p
		set p.pro_amount = (select (p.pro_amount-op.pro_amount) as remaining_amount
							from product p,
							    order_pro op
							where p.pro_idx=op.pro_idx
								and op.order_idx = #{order_idx}
								and p.pro_idx=#{pro_idx}
							)
		where p.pro_idx in (select op.pro_idx
                    		from order_pro op
                    		where op.order_idx = #{order_idx}
								and op.pro_idx=#{pro_idx}
                    		)

mapper.select:
select count(*)
from product 
where pro_thumb=#{pro_thumb}

mapper.update:
		update product p
		set p.pro_amount=p.pro_amount + op.pro_amount
		where p.pro_idx in (select op.pro_idx
                    		from order_pro op
                    		where op.order_idx=#{order_idx}
                                and op.pro_idx=#{pro_idx}
                    		)
