mapper.update:
update turnback t
set t.return_ok='N'
where t.order_idx=#{order_idx}
	and t.pro_idx=#{pro_idx}

mapper.select:
select * from (select rownum as rnum,a.* from ( SELECT * FROM orders o JOIN order_pro op ON o.order_idx = op.order_idx JOIN product p ON op.pro_idx = p.pro_idx JOIN turnback t ON o.order_idx = t.order_idx WHERE o.order_idx=op.order_idx and o.order_idx=t.order_idx and op.pro_idx=p.pro_idx and pro_name like #{fvalue}
and (order_status = 5)
and (order_status = 6)
and (order_status = 7)
and (order_status = 8)
order by order_date desc ) a ) b where rnum >= #{start} and rnum <= #{end}
