mapper.select:
select * from
(select rownum as rnum, a.*from(
	select * from review order by review_idx desc
)a )b
where rnum>=#{start} and rnum<=#{end}

select * from
(select rownum as rnum, a.*from(
	select * from review where user_idx=#{user_idx} order by review_idx desc
)a )b
where rnum>=#{start} and rnum<=#{end}

select count(*) from
	review
where user_idx=#{user_idx}

		select * from
		(select rownum as rnum, c.*from(
			select svc_idx activity_idx, user_idx , user_name writer
  			from svc_member a
 			where a.user_idx = #{user_idx}
				and svc_state='작업완료'
				and not exists (select * from review b
								 where a.svc_idx = b.activity_idx)			
			)c 
		)d
		where rnum>=#{start} and rnum<=#{end}

mapper.update:
update review
set readnum=readnum+1
where review_idx=#{review_idx}

update review
set subject=#{subject}, content=#{content}, thumb=#{thumb} , star=#{star}
where review_idx=#{review_idx}
